import React from "react";
import Card from "react-bootstrap/Card";
import AddPokemonButton from "../AddPokemonButton/AddPokemonButton";
import DeletePokemonButton from "../DeletePokemonButton/DeletePokemonButton";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./style.css";
import PokemonProfile from "../../components/PokemonProfile/PokemonProfile";

function TrainerProfile(props) {

    // Retrieve pokemon data from each trainer
    let getPokemons = (e) => {
        fetch("http://localhost:3001/api/" + this.props.name + "/pokemons")
        .then(async (response) => {
            let jsonResponse = await response.json();
            console.log("Trainer's Pokemon(s):", jsonResponse);

            // store in state 
            this.setState({
                pokemons: jsonResponse
            });
        }).catch(error => console.log(error));
    }

    // Map the pokemon's data from db
    let getPokemonsProfile = this.state.pokemons.map(pokemon => {
        return (
            <PokemonProfile 
                key={pokemon.id}
                id={pokemon.id}
                pokemonName={pokemon.name}
                type={pokemon.type}
                move={pokemon.move}
            />
        )
    })

    return (
        <div>
            <Container className="trainer-div">
                <Row className="justify-content-md-center">
                    <Col md="8">
                        <Card className="justify-content-md-center">
                            <Card.Header as="h5">
                                {props.name}
                            </Card.Header>
                            <Card.Body>
                                <Card.Text>
                                    <span className="list">
                                        {getPokemonsProfile}
                                    </span>
                                </Card.Text>
                                <Row>
                                    <Col><AddPokemonButton className="addButton"></AddPokemonButton></Col>
                                    <Col><DeletePokemonButton className="deleteButton"></DeletePokemonButton></Col>
                                </Row>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    )

}

export default TrainerProfile;
