import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import TrainerModal from "../TrainerModal/TrainerModal";


// Add trainer button is imported 
// Once clicked, modal/form shows
function AddTrainerButton(props) {
    const [state, setState] = useState({
        modalShow: false
    });

    // create hideModal function and set the state to false
    let hideModal = () => {
        setState({
            modalShow: false
        })
    }
    // create showModal function and set to true (so that modal will show up once button is clicked)
    let showModal = () => {
        setState({
            modalShow: true
        })
    }


    return (
        <div> 
            <Button 
                variant="danger" 
                onClick={() => showModal()} >
                Add Trainer
            </Button>
            <TrainerModal 
                onHide={() => hideModal()} 
                show={state.modalShow}
            />
        </div>
    )

}

export default AddTrainerButton;
