// Actions are what you want to do
// Add and Delete trainer and pokemon

// ===== TRAINER ACTIONS =====
export const addTrainer = (data) => {
    return {
        type: 'ADD TRAINER',
        data: data
    };
}

export const deleteTrainer = (id) => {
    return {
        type: 'DELETE TRAINER',
        id: id
    };
}

export const searchTrainer = (data) => {
    return {
        type: 'SEARCH TRAINER',
        data: data
    };
}

// ===== POKEMON ACTIONS =====
export const addPokemon = (data) => {
    return {
        type: 'ADD POKEMON',
        data: data
    };
}

export const deletePokemon = (id) => {
    return {
        type: 'DELETE POKEMON',
        id: id
    };
}

export const searchPokemon = (data) => {
    return {
        type: 'SEARCH POKEMON',
        data: data
    };
}
