import React,{ Component } from "react";
import Button from "react-bootstrap/Button";
import TrainerModal from "../TrainerModal/TrainerModal";

// Add trainer button is imported 
// Once clicked, modal/form shows

class AddTrainerButton extends Component {
    // create a modalShow state that is set to false (once clicked it becomes true)
    constructor(props) {
        super(props);
        this.state = {
           modalShow: false
        };
    }
    render() {
        return (
            <div> 
                <Button variant="info">Add Trainer</Button>
                <TrainerModal/>
            </div>
        )
    }
}

export default AddTrainerButton;
