import React,{ Component } from "react";
import Navbar from "react-bootstrap/Navbar";
import FormControl from "react-bootstrap/FormControl";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import TrainerProfile from "../../components/TrainerProfile/TrainerProfile";

class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.state = {
            search: ""
        };
    }

    // Create function for search bar 
    // take the user's input and make a search
    // make a fetch call to db
    makeSearch(e) {
      const searchInput = e.target.value;
      if(e.keyCode === 13) {
        console.log(e.target.value);
        console.log("Enter was pressed");
        fetch("http://localhost:3001/api/search/" + e.target.value)
        .then(async (response) => {
          let jsonResponse = await response.json();
          console.log("Search results:", jsonResponse);

          this.setState({
            searchResults: jsonResponse,
            searchTerm: searchInput
          });
        }).catch(error => console.log(error));
      }

    }


    render () {
      let search = this.state.searchResults.map(search => {
        return (
          <TrainerProfile
            key={search.id}
            id={search.id}
            searchName={search.name}
            type={search.type}
            move={search.move}
            />
        )
      })
      return (
        <div>

          <Navbar bg="dark" variant="dark">
            <Col md={7}>
              <Navbar.Brand href="#home">Pok√© Trainer Stats</Navbar.Brand>
              <Navbar.Toggle aria-controls="basic-navbar-nav" />
            </Col>
            <Col md={5}>
              <Navbar.Collapse id="basic-navbar-nav">
                <Form inline>
                  <FormControl type="text" placeholder="Search" className="mr-sm-2 searchBar" />
                  <Button variant="outline-success">Search</Button>
                </Form>
              </Navbar.Collapse>
            </Col>
          </Navbar>
          {search}
        </div>
      )
    }
}

export default SearchBar;
