import React, { Component }from 'react';
import './App.css';
import SearchBar from './components/SearchBar/SearchBar.jsx';
import AddTrainerButton from './components/AddTrainerButton/AddTrainerButton';
import DeleteTrainerButton from './components/DeleteTrainerButton/DeleteTrainerButton';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from 'react-bootstrap/Container';
import Profile from './components/Profile/Profile';
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trainer: [],
      pokemon: []
    }
  }
    // some function:

    getAllTrainersAndPokemon = (e) => {
      // Make a get request from UserLikes db in the UserId column which gets their liked items
      // will be called in wishListClick
      axios.get('api/trainer', {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token")
        }
      })
        .then(response => {
          console.log("User likes: ", response);
          // using setState, we save the response in the state: wishCards state
          this.setState({
            wishCards: response.data
          })
        })
        .catch(function (error) {
          console.log(error);
        })

    };
    // call API to get trainers+pokemons
    // store in state (e.g. state.trainers)
  render () {

    // render:
    // loop through all state.trainers
    //     TrainerA --> <TrainerProfile trainer={trainer}
    //         pokemonA1 --> <PokemonProfile pokemon={trainer.pokemons}
    //         pokemonA2
    //         ...
    //     TrainerB
    //         pokemonB1
    //         pokemonB2
    //         ...
    //     TrainerC
    //         pokemonC1
    //         pokemonC2
    //         ...
    //componentdidmount => make api call

    return (
      <div className="App">
        <SearchBar/>
        <Container className="trainerButtons">
          <Row className="justify-content-lg-center">
            <Col md="6">
              <AddTrainerButton/>
            </Col>
            <Col md="6">
              <DeleteTrainerButton/>
            </Col>
          </Row>
        </Container>
        <Profile/>
      </div>
    );
  }

}

export default App;
