import React,{ Component } from "react";
import Navbar from "react-bootstrap/Navbar";
import FormControl from "react-bootstrap/FormControl";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";

class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.state = {
            search: ""
        };
    }

    // Create function for search bar 
    // take the user's input and make a search
    // make a fetch call to db
    makeSearch(e) {
      const searchInput = e.target.value;
      if(e.keyCode === 13) {
        console.log(e.target.value);
        console.log("Enter was pressed");
        fetch("http://localhost:3000/api/searchresults/trainer/" + this.props.name)
        .then(async (response) => {
          let jsonResponse = await response.json();
          console.log("Search results:", jsonResponse);

          this.setState({
            searchResults: jsonResponse,
            searchTerm: searchInput
          });
        }).catch(error => console.log(error));
      }

    }


    render () {
      return (
        <Navbar bg="dark" variant="dark">
          <Col md={7}>
            <Navbar.Brand href="#home">Pok√© Trainer Stats</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
          </Col>
          <Col md={5}>
            <Navbar.Collapse id="basic-navbar-nav">
              <Form inline>
                <FormControl type="text" placeholder="Search" className="mr-sm-2 searchBar" />
                <Button variant="outline-success">Search</Button>
              </Form>
            </Navbar.Collapse>
          </Col>
        </Navbar>
      )
    }
}

export default SearchBar;
