import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import "./style.css";
import { getAddPokemonAction} from "../../actions";
import { connect } from "react-redux";

function pokemonForm(props) {



  return (
    <Form className="pokemonForm">
      <Form.Group controlId="formBasic">
        <Form.Label>Pokemon</Form.Label>
        <Form.Control type="text" placeholder="Pokemon name" />
      </Form.Group>

      <Form.Group controlId="formBasic">
        <Form.Label>Type</Form.Label>
        <Form.Control type="text" placeholder="Enter type" />
      </Form.Group>

      <Form.Group controlId="formBasic">
        <Form.Label>Move</Form.Label>
        <Form.Control type="text" placeholder="Enter moves" />
      </Form.Group>

      <Button 
        variant="primary" 

        onClick={() => {
          props.onHide();
          props.submitForm({
              id: 1,
              name: "Psyduck",
              type: "psychic",
              move: "confusion"
            },
            props.trainerName
        )}}     
        >
        Submit
      </Button>
    </Form>
  )
}

const mapDispatchToProps = (dispatch) => {

  return {
    submitForm: (data, trainerName) => {
      console.log("Submitted Pokemon Form!", data);
      dispatch(getAddPokemonAction(data));
    }
  }
}

// Given updated state build new props for pokemonForm
const mapStateToProps = (state) => {
  return {
    trainerName: state.trainerReducer.addToMe
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(pokemonForm);
