import React,{Component} from "react";
import Card from "react-bootstrap/Card";
import AddPokemonButton from "../AddPokemonButton/AddPokemonButton";
import DeletePokemonButton from "../DeletePokemonButton/DeletePokemonButton";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./style.css";

class Profile extends Component {
    // Render pokemons after data is fetched from db
    componentDidMount() {
        this.getPokemons();
    }

    // Retrieve pokemon data from each trainer
    getPokemons = (e) => {
        fetch("http://localhost:3000/api/:trainer/pokemons")
        .then(async (response) => {
            let jsonResponse = await response.json();
            console.log("Pokemons:", jsonResponse);

            // store in state 
            this.setState({
                pokemons: jsonResponse
            });
        }).catch(error => console.log(error));
    }

    render() {

        // // Map the pokemon's data from db
        // let getPokemonsProfile = this.state.pokemons.map(pokemon => {
        //   return (
        //       <Profile 
        //         key={pokemon.id}
        //         id={pokemon.id}
        //         pokemonName={pokemon.name}
        //         type={pokemon.type}
        //       />
        //   )
        // })
        return(
            <div>
                <Container>
                    <Row className="justify-content-md-center">
                        <Col md="8">
                            <Card className="justify-content-md-center">
                                <Card.Header as="h5">{this.props.name}</Card.Header>
                                <Card.Body>
                                    <Card.Text>
                                        <span className="list">
                                            <li>Pokemon Name: {this.props.pokemonName}</li>
                                            <li>Type:</li>
                                            <li>Move:</li>
                                        </span>
                                    </Card.Text>
                                    <Row>
                                        <Col><AddPokemonButton className="addButton"></AddPokemonButton></Col>
                                        <Col><DeletePokemonButton className="deleteButton"></DeletePokemonButton></Col>
                                    </Row>


                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>


            </div>
        )
    }
}

export default Profile;
