// Actions are what you want to do
// Add and Delete trainer and pokemon

// ===== TRAINER ACTIONS =====
export const getTrainersAction = () => {

    return async (dispatch, getState) => {
        let fetchPromise = fetch("http://localhost:3001/api/trainers");
        let response = await fetchPromise;
        let jsonResponse = await response.json();
        console.log("Response from fetching trainers:", jsonResponse);

        dispatch({
            type: "FETCHED TRAINERS FROM API",
            data: jsonResponse
        });
    }
};

export const addTrainer = (data) => {
    return {
        type: 'ADD TRAINER',
        data: data
    };
}

export const getDeleteTrainerAction = (name) => {
    return {
        type: 'DELETE TRAINER',
        name: name
    };
}

export const searchTrainer = (data) => {
    return {
        type: 'SEARCH TRAINER',
        data: data
    };
}

// ===== POKEMON ACTIONS =====
export const getAddPokemonAction = (data) => {
    return {
        type: 'ADD POKEMON',
        data: data
    };
}

export const getDeletePokemonAction = (id, name) => {
    return {
        type: 'DELETE POKEMON',
        id: id,
        name: name
    };
}

export const searchPokemon = (data) => {
    return {
        type: 'SEARCH POKEMON',
        data: data
    };
}

export const getAddToMeAction = (data) => {
    return {
        type: 'ADD TO ME',
        data: data
    }
}
