// create a reducer that will keep track of added trainers 
// and pokemon

// store initializes store with 0 trainers
const initialState = {trainers: []};

const trainerReducer = (state = initialState, action) => {
    switch(action.type) {

        case "ADD TRAINER":
            console.log("Case ADD TRAINER", action);
            console.log("state", state);
            // https://redux.js.org/basics/reducers
            const newState = Object.assign({}, state, {trainers: [...state.trainers, action.data]});
            console.log(newState);
            return newState;
        default: 
            return state;
    }
}
export default trainerReducer;
