import React, { Component }from 'react';
import './App.css';
import SearchBar from './components/SearchBar/SearchBar.jsx';
import AddTrainerButton from './components/AddTrainerButton/AddTrainerButton';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from 'react-bootstrap/Container';
import TrainerProfile from './components/TrainerProfile/TrainerProfile';
import { connect } from 'react-redux';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trainers: [],
      pokemons: []
    }
  }

  // Render trainers after data is fetched from db
  componentDidMount() {
    this.getTrainers();
  }

  // call API to get trainers+pokemons
  getTrainers = (e) => {
    fetch("http://localhost:3001/api/trainers")
    .then( async (response) => {
      let jsonResponse = await response.json();
      console.log("Trainers:", jsonResponse);
      // store in state (e.g. state.trainers)

      this.setState({
        trainers: jsonResponse
      });
    }).catch(error => console.log(error));
  }

  render () {
    // Map the trainer's data from db
    let getTrainerProfile = this.state.trainers.map(trainer => {
      return (
        <TrainerProfile 
          key={trainer.id}
          id={trainer.id}
          name={trainer.name}
          pokemon_owned={trainer.pokemon_owned}
        />
      )
    })

    // map search results in searchbar
    return (
      <div className="App">
        <SearchBar/>
        <Container className="trainerButtons">
          <Row className="justify-content-lg-center">
            <Col md="6">
              <AddTrainerButton />
            </Col>
            <Col md="6">
            </Col>
          </Row>
          {getTrainerProfile}
        </Container>
      </div>
    );
  }
}

export default connect(mapStateToProps)(App);
