import React, { Component }from 'react';
import './App.css';
import SearchBar from './components/SearchBar/SearchBar.jsx';
import AddTrainerButton from './components/AddTrainerButton/AddTrainerButton';
import DeleteTrainerButton from './components/DeleteTrainerButton/DeleteTrainerButton';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from 'react-bootstrap/Container';
import Profile from './components/Profile/Profile';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trainers: [],
      pokemons: []
    }
  }
  // Render trainers after data is fetched from db
  componentDidMount() {
    this.getTrainers();

  }

  // call API to get trainers+pokemons
  getTrainers = (e) => {
    fetch("http://localhost:3000/api/trainers")
    .then( async (response) => {
      let jsonResponse = await response.json();
      console.log("Trainers:", jsonResponse);
      // store in state (e.g. state.trainers)

      this.setState({
        trainers: jsonResponse
      });
    }).catch(error => console.log(error));
  }

  render () {
    // Map the trainer's data from db
    let getTrainerProfile = this.state.trainers.map(trainer => {
      return (
        <Profile 
          key={trainer.id}
          id={trainer.id}
          name={trainer.name}
          pokemon_owned={trainer.pokemon_owned}
        />
      )
    })

    return (
      <div className="App">
        <SearchBar/>
        <Container className="trainerButtons">
          <Row className="justify-content-lg-center">
            <Col md="6">
              <AddTrainerButton/>
            </Col>
            <Col md="6">
              <DeleteTrainerButton/>
            </Col>
          </Row>
          {getTrainerProfile}
          {getPokemonsProfile}
        </Container>

      </div>
    );
  }


}

export default App;
