import React, { Component }from 'react';
import './App.css';
import SearchBar from './components/SearchBar/SearchBar.jsx';
import AddTrainerButton from './components/AddTrainerButton/AddTrainerButton';
import DeleteTrainerButton from './components/DeleteTrainerButton/DeleteTrainerButton';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from 'react-bootstrap/Container';
import Profile from './components/Profile/Profile';
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trainers: [],
      pokemons: []
    }
  }
  // Fetch data
  componentDidMount() {
    this.getTrainersAndPokemon();
  }



  // some function:
  getTrainersAndPokemon = (e) => {
    // call API to get trainers+pokemons
    // store in state (e.g. state.trainers)
    axios.get("/api/trainers", {
      params: {
        trainers: this.props.name
      }
    })
    .then(response => {
      console.log("Trainers and pokemon:",response);
      this.setState({
        trainers: response.data
      });
    }).catch(error => console.log(error));
  }

  render () {
    // render:
    // loop through all state.trainers
    //     TrainerA --> <TrainerProfile trainer={trainer}
    //         pokemonA1 --> <PokemonProfile pokemon={trainer.pokemons}
    //         pokemonA2
    //         ...
    //     TrainerB
    //         pokemonB1
    //         pokemonB2
    //         ...
    //     TrainerC
    //         pokemonC1
    //         pokemonC2
    //         ...
    //componentdidmount => make api call
      let trainerProfile = this.state.profile.map(data => {
        return (
          <Profile  
            key={data.id}
            name={data.name}
        />
        )
      })

      return (
        <div className="App">
          <SearchBar/>
          <Container className="trainerButtons">
            <Row className="justify-content-lg-center">
              <Col md="6">
                <AddTrainerButton/>
              </Col>
              <Col md="6">
                <DeleteTrainerButton/>
              </Col>
            </Row>
            {trainerProfile}
          </Container>

        </div>
      );
  }

}

export default App;
