// create a reducer that will keep track of added trainers 
// and pokemon

// store initializes store with 0 trainers
const state = {trainers: []};

const trainerReducer = (state = state, action) => {
    switch(action.type) {

        case "ADD TRAINER":
            console.log("Case ADD TRAINER", action);
            state.trainers.push(action.data);
            console.log(state);
            return state;
        default: 
            return state;
    }
}
export default trainerReducer;
