import React,{ Component } from "react";
import Button from "react-bootstrap/Button";
import TrainerModal from "../TrainerModal/TrainerModal";

// Add trainer button is imported 
// Once clicked, modal/form shows

class AddTrainerButton extends Component {
    // create a modalShow state that is set to false (once clicked it becomes true)
    constructor(props) {
        super(props);
        this.state = {
           modalShow: false
        };
    }
    // create hideModal function and set the state to false
    hideModal() {
        this.setState({
            modalShow: false
        })
    }
    showModal() {
        this.setState({
            modalShow: true
        })
    }
    render() {
        return (
            <div> 
                <Button variant="info" onClick={() => this.showModal()} >Add Trainer</Button>
                <TrainerModal onHide={() => this.hideModal()} show={this.state.modalShow}/>
            </div>
        )
    }
}

export default AddTrainerButton;
