import React,{ Component } from "react";
import Button from "react-bootstrap/Button";
import PokemonModal from "../PokemonModal/PokemonModal";

class AddPokemonButton extends Component {
    // create a modalShow state that is set to false (once clicked it becomes true)
    constructor(props) {
        super(props);
        this.state = {
           modalShow: false
        };
    }
    // create hideModal function and set the state to false
    hideModal() {
        this.setState({
            modalShow: false
        })
    }
    // create showModal function and set to true (so that modal will show up once button is clicked)
    showModal() {
        this.setState({
            modalShow: true
        })
    }
    render() {
        return (
            <div>
                <Button variant="primary" onClick={() => this.showModal()}>Add Pokemon</Button>
                {/* <PokemonModal onHide={() => this.hideModal()} show={this.state.modalShow}/> */}
            </div>
        )
    }
}

export default AddPokemonButton;
