class Patient extends Component {
    // create a constructor that takes in props
    constructor(props) {
        super(props);

        this.state = {
            year: 0,
            yearValid: false
        };
    }

    checkYear(e) {
        console.log(e.target.value);
        // Check year if it is correct
        let year = e.target.value;
        // Years between: 1900 - 2000
        let valid = false;


        // Write a condition to check the year 
        if(year >= 1900 && year <= 2000) {
            valid = true;
            // update the year in the state
            this.setState({ year: year });

        }

        // Update yearValid state after condition is checked
        this.setState({ yearValid: valid });

