// create a reducer that will keep track of added trainers 
// and pokemon

// store initializes store with 0 trainers
const defaultTrainers = [];

const trainerReducer = (trainers = defaultTrainers, action) => {
    switch(action.type) {

        case "ADD TRAINER":
            console.log("Case ADD TRAINER", action);
            trainers.push(action.data);

            return trainers;
        default: 
            return trainers;
    }
}
export default trainerReducer;
