                <AddTrainerButton/>
              </Col>
              <Col md="6">
                <DeleteTrainerButton/>
              </Col>
            </Row>
          </Container>
          <Profile/>
          <ul>
            {items.map(item => (
              <li key={item.id}>
                {item.name}
              </li>
            ))}
          </ul>
        </div>
      );
    }
  }

}

export default App;
