import React from "react";
import Button from "react-bootstrap/Button";
import PokemonModal from "../../components/PokemonModal/PokemonModal";

function AddPokemonButton(props) {
    const [state, setState] = useState({
        modalShow: false
    });

    // create hideModal function and set the state to false
    let hideModal = () => {
        setState({
            modalShow: false
        })
    }
    // create showModal function and set to true (so that modal will show up once button is clicked)
    let showModal = () => {
        console.log("STATE", state);
        setState({
            modalShow: true
        })
    }


    return (
        <div>
            <Button 
                variant="success" 
                onClick={() => showModal()} 
            >
                Add Pokemon
            </Button>
            <PokemonModal 
                onHide={() => hideModal()} 
                show={state.modalShow}
            />
        </div>
    )

}

export default AddPokemonButton;
